#!/usr/bin/env node

import { writeFileSync, mkdirSync } from 'fs';
import { dirname } from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const buildDate = new Date().toISOString();

const content = `// This file is auto-generated by scripts/generate-build-info.js
// Do not edit manually
export const BUILD_DATE = '${buildDate}';
`;

const outputPath = new URL('../src/lib/build-info.ts', import.meta.url).pathname;

try {
	mkdirSync(dirname(outputPath), { recursive: true });
	writeFileSync(outputPath, content);
	console.log(`Build info generated: ${buildDate}`);
} catch (error) {
	console.error('Failed to generate build info:', error);
	process.exit(1);
}
